print("UDP")from socket import*from datetime import*ip = gethostbyname(gethostname())port = 2145print("IP = ",ip)print("Recibiendo Datos...")print("Recibiendo En Puerto:", port)while True: ip = gethostbyname(gethostname()) print(" ") ServerSock = socket(AF_INET, SOCK_DGRAM) ServerSock.bind((ip, port)) DatRec, addr = ServerSock.recvfrom(1024) fix = 0.00001 fix2 = 0.0001 contenido=str(DatRec) signolatit = (contenido[18]) signolong = (contenido[26]) longt = (contenido[27:35]) latit = (contenido[19:26]) longt = float(longt) latit = float(latit) longt1 = (longt*fix) latit1 = (latit*fix) longt2 = ("%.5f" %longt1) latit2 = ("%.5f" %latit1) #ID = (contenido[42:60]) print(contenido) dates = datetime.now() print("Latitud =",signolatit,latit2," Longitud =",signolong,longt2)  variables = open("Coordenadas.txt", "w") variables.write("Latitud: %s%s Longitud: %s%s           Fecha: %s/%s/%s           Hora: %s:%s:%s"% (str(signolatit),str(latit2),str(signolong), str(longt2),dates.day, dates.month, dates.year, dates.hour, dates.minute, dates.second)) variables.close()  